mapper.update:
UPDATE
homestay.order t
set t.clean_status_code = #{cleanStatusCode}
WHERE t.order_id = #{orderId}

mapper.select:
SELECT count(1) count FROM homestay.order t INNER JOIN homestay.homestay_info a ON t.homestay_id = a.homestay_id WHERE 1 = 1 AND t.clean_status_code = 1
AND DATE_FORMAT(t.create_date,'%Y-%m-%d') = #{currentDate}
AND a.homestay_address = #{address}
GROUP BY a.homestay_address

SELECT a.homestay_name homeStayBreand, a.homestay_address address, c.room_id roomNo, CASE c.room_type WHEN '0' THEN '1' WHEN '1' THEN '2' WHEN '2' THEN '3' END roomType, CASE t.clean_type WHEN '0' THEN '1' WHEN '1' THEN '2' END clearType, b.boss_cost expense, t.clean_status_code state, t.order_id orderId FROM homestay.order t JOIN homestay.order_record b ON t.order_id = b.order_id JOIN homestay.homestay_info a ON t.homestay_id = a.homestay_id JOIN homestay.room_info c ON t.room_id = c.room_id WHERE 1=1
AND (t.clean_status_code = #{cleanStatusCode1} OR t.clean_status_code = #{cleanStatusCode2})
AND t.clean_status_code = #{cleanStatusCode}

SELECT CASE c.room_type WHEN '0' THEN '1' WHEN '1' THEN '2' WHEN '2' THEN '3' END roomType, CASE t.clean_type WHEN '0' THEN '1' WHEN '1' THEN '2' END clearType,
b.actual_clean_date date, c.room_id roomNo, b.boss_cost expense FROM homestay.order t JOIN homestay.order_record b ON t.order_id = b.order_id JOIN homestay.homestay_info a ON t.homestay_id = a.homestay_id JOIN homestay.room_info c ON t.room_id = c.room_id WHERE 1=1 AND t.clean_status_code = 3 AND a.operators_telephone = #{operatorPhone}
AND DATE_FORMAT(b.actual_clean_date,'%Y-%m-%d') = #{date}
AND t.room_id = #{roomNo}
AND a.homestay_address = #{place}
